$(document).ready(function(){$("body").bootstrapMaterialDesign();$("#filter-check input").on("click",()=>{tags=[];if($("#filter-check :checkbox:checked").length<0){$(".filter-div").show()}$("#filter-check :checkbox:checked").each(function(){tags.push($(this).val());tags.filter(tag=>tag!==$(this).val())});function checkTags(div){let tagsArray=$(div).attr("data-tags").trim().split(" ");if(tags.every(val=>tagsArray.includes(val))){$(div).show()}else{$(div).hide()}}const divsList=$(".filter-div");[...divsList].forEach(div=>{checkTags(div)})})});