$(document).ready(function(){$(".like-btn").on("click",function(e){recipe_id=$(this).data("id");$.ajax({url:`/like/${ recipe_id }`,type:"post",success:function(response){console.log(response)},error:function(xhr){console.log(xhr)}});e.preventDefault();$(this).toggleClass("like-red");count=Number($(`#like-counter-${ recipe_id }`).html());plus_count=count+1;minus_count=count-1;if($(this).hasClass("like-red")){$(`#like-counter-${ recipe_id }`).html(`${ plus_count }`)}else{$(`#like-counter-${ recipe_id }`).html(`${ minus_count }`)}})});